mapper.select:
SELECT `id`,`mainTeam`,`clientTeam`,`halfScore`,`totalScore`,`match`,`round`,`matchTime`,`status` FROM `match`
WHERE DATE(`matchTime`) = #{matchTime}
ORDER BY `matchTime` DESC;

SELECT `id`,`mainTeam`,`clientTeam`,`match`,`round`,`matchTime`,`status`
FROM `match` WHERE `status` = '未开始'
ORDER BY DATE(`matchTime`),`round`;
